import { Directive, Input, ElementRef, HostListener } from '@angular/core';
let missingDefault = `data:image/jpeg;base64,`;
@Directive({
  selector: '[odImgFallback]'
})
export class ImgFallbackDirective {
  el: HTMLImageElement;
  @Input() public odImgFallback: string;

  constructor(private elem: ElementRef) {
    this.el = this.elem.nativeElement;
  }

  ngAfterViewInit() {
    if (!this.el.src)
      this.el.src = missingDefault;
  }

  @HostListener('error', ['$event'])
  onError(e) {
    if (this.el.src !== missingDefault)
      this.el.src = missingDefault;
  }
}
